        // FunÃ§Ã£o auxiliar para formatar tempo em HH:MM:SS
        const formatarTempo = (segundos) => {
            if (!segundos || segundos < 0) return "00:00:00";
            const h = Math.floor(segundos / 3600);
            const m = Math.floor((segundos % 3600) / 60);
            const s = Math.floor(segundos % 60);
            return `${h.toString().padStart(2, '0')}:${m.toString().padStart(2, '0')}:${s.toString().padStart(2, '0')}`;
        };
        
        // FunÃ§Ã£o auxiliar para formatar tempo curto (ex: 2h 30min)
        const formatarTempoCurto = (segundos) => {
            if (!segundos || segundos < 0) return "0min";
            const h = Math.floor(segundos / 3600);
            const m = Math.floor((segundos % 3600) / 60);
            if (h > 0) return `${h}h ${m}min`;
            return `${m}min`;
        };
        
