                    if (t.data_prazo) {
                        const dataVenc = new Date(t.data_prazo);
                        dataVenc.setHours(0, 0, 0, 0);
                        return dataVenc <= hoje; // Hoje ou vencida
                    }
                    return true; // Sem data = aparece no meu dia
